import React, { useState, useCallback } from "react";

import "./App.css";
import Button from "./components/UI/Button/Button";
import DemoOutput from "./components/Demo/DemoOutput";

function App(props) {
  const [showParagraph, setShowParagraph] = useState(false);
  const [allowToggle, setAllowToggle] = useState(false);
  console.log("runningggg");

  const setShowParagraphHander = useCallback(() => {
    if (allowToggleHandlder) {
      setShowParagraph((prevShowParagraph) => !prevShowParagraph);
    }
  }, []);

  const allowToggleHandlder = () => {
    setAllowToggle(true);
  };
  return (
    <div className="app">
      <h1>Hi there!</h1>
      <DemoOutput show={allowToggleHandlder} />

      <Button onClick={setShowParagraphHander}>Allow toggle</Button>
      <Button onClick={setShowParagraphHander}>show paragraph</Button>
    </div>
  );
}

export default App;
